import React ,{useState} from 'react'



function Login(props) {
   const [username,setUsername] =useState('');
   const [password,setPassword] =useState('');
//    const [error,setError] =useState(null);
//    const [loading,setLoading] =useState(false);

  const handleLogin =()=>{
    props.history.push('./register')
  }

  return (
  <div>
     <div className="top">
       <div><p>Todos</p></div>
       </div>
    <div className="container">
        <div className="content" >
            <div className="topic">
                <lable id="title"><strong>Sign in</strong></lable>
                <div id="main" align="center">
                    <input type="text" id="username" placeholder="Username" value={username} onChange={e =>setUsername(e.target.event)}/>
                    <input type="password" id="pass" placeholder="Password" value={password} onChange={e =>setPassword(e.target.event)} />
                    <div>
                        <button id="butclasston" onClick={handleLogin}><b>Sign in</b></button>
                    </div>
                    <div className="direct">
                        <a href="/">New here? Register now</a>
                    </div>
                </div>
            </div>
        </div>
    </div>

    </div>

  )
}

export default Login
