import React ,{useState} from 'react'
import {useNavigate,NavLink} from 'react-router-dom'
import axios from './api/axios';

// const REGISTER_URL = '/';

function Login() {

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [userError,setUserError] = useState('');
    const [pwdError, setPwdError] = useState('');
    const navigate =useNavigate('');
    const getuserArr=localStorage.getItem("userdetail");

    const handleSubmit = async (e) => {
        e.preventDefault('');
       if(!user){
           setUserError("Please add your username")
           return;
       } else if (!pwd){
       setPwdError("Please add your password")
       return;
       }

    // let inpValue={user,pwd}
    // console.log(inpValue);
    // if(user===""){
    //     alert("name field is required");
    // }
    // else if(pwd===""){
    //     alert("password field is required");
    // } else if(pwd.length<5){
    //     alert("password length greater 5");
    // }
    // else{
    //     if(getuserArr && getuserArr.length){
    //         const userdata=JSON.parse(getuserArr);
    //         const userlogin=userdata.filter((el,k)=>{
    //             return el.user===user && el.pwd===pwd
    //         });
    //         if(userlogin.length===0)
    //         {
    //             alert("invalid details");
    //         }else{

    //             console.log("user login successfully");
    //             navigate("/todo");
    //         }
    //     }
       try {

        const response = await axios.post(REGISTER_URL,
            JSON.stringify({ user, pwd }),
            {
                headers: { 'Content-Type': 'application/json' },
                withCredentials: true
            }

        );
        setUser('');
        setPwd('');
        navigate('/todo')
    } catch (err) { 


    }
 }


  return (
  <div>

    <div className="container">
    <div className="top">
       <div><h3>Todos</h3></div>
       </div>
        <div className="content" >
            <div className="topic">
                <lable id="title"><strong>Sign in</strong></lable>
                <div id="main" align="center">
                    <input type="email"
                            id="username"
                            onChange={(e) => setUser(e.target.value)}
                            value={user}
                            required
                             placeholder="Username"

                             />
                             {userError && (<p>{userError}</p>)}
                    <input  type="password"
                            id="password"
                            onChange={(e) => setPwd(e.target.value)}
                            value={pwd}
                            required
                             placeholder="Password" />
                              {pwdError && (<p>{pwdError}</p>)}
                    <div>
                        <button id="butclasston" onClick={handleSubmit}><b>Sign in</b></button>
                    </div>
                    <div className="direct">
                      <NavLink to={"/register"}>New here? Register now</NavLink>
                    </div>
                </div>
            </div>
        </div>
    </div>

    </div>

  )
}

export default Login
